      <div class="boxed">
        {this.state.currentChallenge.Title}
        <div class = "button" id = "button">
          <button type="button" class="btn btn-primary btn-lg btn-block" onClick={clicked}> Complete</button>
        </div>
      </div>
    </div>
    );
  }else{
    return(
      <div />
    )
  }

 }
}

