import React, {Component} from 'react';
import './App.css';
import challenges from './challengesList.js';
import './challenges.css';
import UserPage from './userPage.js';
import PushChallenge from './pushchallenge';


class CallChallenges extends Component{

  constructor(props){
    super(props);
    this.state = {
      currentChallenge: challenges[(Math.floor(Math.random() * challenges.length))],
      click: false,
      user: props.username
    }
  }


 clicked = () => {
   this.setState({
     click: true
   });
 };


 render(){
   if(this.state.click===true) {
    return(
    <div>
        <div class="boxed">
          {this.state.currentChallenge.Title}
          <div class = "button" id = "button">
            <button type="button" class="btn btn-primary btn-lg btn-block" disabled> Completed daily task</button>
          </div>
          <a href='/userPage' >Challenge Completed!</a>

        </div>
        <PushChallenge challenge={this.state.currentChallenge} />
    </div>
    );
   }else{
    return(
      <div class="boxed">
        {this.state.currentChallenge.Title}
        <div class = "button" id = "button">
          <button type="button" class="btn btn-primary btn-lg btn-block" onClick={() => this.clicked()}> Complete</button>
        </div>
      </div>
    );
   }
 }
}
export default CallChallenges;
