import React, {Component} from 'react';
import firebase from './Firestore';
import StyledFirebaseAuth from 'react-firebaseui/StyledFirebaseAuth';

const uiConfig = {
  signInFlow: 'popup',
  callbacks: {
    signInSuccessWithAuthResult: () => false
  },
  signInOptions: [
    firebase.auth.EmailAuthProvider.PROVIDER_ID
  ]
};
class Login extends Component {

  constructor(){
    super();
    this.state = {signedIn: false, currentUser: null};
  }

  componentWillMount(){
    firebase.auth().onAuthStateChanged(user => {
      if (user) {
        this.setState({
          signedIn: true,
          currentUser: user
        });

        const db = firebase.firestore();
        db.settings({
          timestampsInSnapshots: true
        });

        const userRef = db.collection("UserData");

        userRef.doc(user.uid).get().then(getDoc => {
          if (!getDoc.exists){
            userRef.doc(user.uid).set({
              username: user.displayName,
              email: user.email,
              footprint: [
                {
                  value: 0,
                  timestamp: new Date(Date.now())
                }
              ],
              challenge: [
                {
                  name: "",
                  value: 1,
                  completion: false
                }
              ]
            })
          }
        })
      }
    })
  }

  signOut = () => firebase.auth().signOut().then(() => {
    this.setState({
      signedIn: false,
      currentUser: null
    })
  })

  render(){
    if (this.state.signedIn) {
      return(
        <div>
          <p>Hello {this.state.currentUser.displayName}</p>
          <button onClick={this.signOut} >Sign Out</button>
        </div>
      );
    } else {
      return(
      <div>
         <h1>Please sign in</h1>
         <div class="container">
	        <div class="main">
		        <div class="logo">
			         <h1>âœ”</h1>
		      </div>
		      <form action="#">
			       <input type="email" placeholder="email" autocomplete="off" required/>	<i class="fa fa-user"></i>
			       <input type="password" placeholder="password" autocomplete="off" required/>	<i class="fa fa-lock"></i>
			       <input type="submit" value="Log In"/>
			       <h4>Not Registered?<a href="#"> Create an Account</a></h4>
		      </form>
	       </div>
        </div>
      </div>
      )
    }
  }
}

export default Login;
