  handleChange(event) {
        event.preventDefault();
        this.setState({
            response1: event.target.response1,
            response2: event.target.response2,
            response3: event.target.response3,
            response4: event.target.response4
        })
    }

  render(){
	return (

    	<form>

          <div>
          How many miles do you drive per day?
          </div>
          <input
            	type = "number"
            	name = "response1"
            	placeholder = "Response"
            	onChange = {this.handleChange(event)}
            	value = {this.state.response1}

        	/>
          <button type = "submit">Submit</button>
          <div>
          How many trash bags do you fill per week?
          </div>
        	<input
            	type = "number"
            	name = "response2"
            	placeholder = "Response"
            	onChange = {this.handleChange(event)}
            	value = {this.state.response2}
        	/>
        	<button type = "submit">Submit</button>
          <div>
          What was the cost of your most recent electricity bill?
          </div>
        	<input
            	type = "number"
            	name = "response3"
            	placeholder = "Response"
            	onChange = {this.handleChange(event)}
            	value = {this.state.response3}
        	/>
        	<button type = "submit">Submit</button>
          <div>
          What is the average amount of time you spend showering per day?
          </div>
        	<input
            	type = "number"
            	name = "response3"
            	placeholder = "Response"
            	onChange = {this.handleChange(event)}
            	value = {this.state.response3}
        	/>
        	<button type = "submit">Submit</button>

    	</form>
	);
    console.log(this.state.response1);
  }

}

export default CarbonForm;
